import pytestfrom user_profile.models import Userfrom borrows.models import BorrowTransactionsfrom reservations.tests.test_reservation_model import create_test_bookfrom reviews.models import ReviewAndRatings@pytest.mark.django_dbdef test_review():    borrower = User.objects.create(username='test username', password='test', email='test@test.com')    book = create_test_book()    transaction = BorrowTransactions.objects.create(borrower=borrower, book=book)    review = ReviewAndRatings.objects.create(transaction=transaction, book=book,                                             review="Test Review", rating=2)    assert review.review == "Test Review"    assert review.book.title == "Test Book"    assert review.transaction.borrower.username == "test username"